        public bool listint2string()
        {
            txtInput2.Text = "";
            for (int i = 0; i < cx.Count; i++)
            {
                txtInput2.Text += cx[i] + " ";
            }

            return true;
        }
        public bool changeStringorChar2IntegerCodeASCII()
        {
            txtDebug.Text = "";
            string x = txtInput.Text;
            int y = 0;
            for (int i = 0; i < txtInput.Text.Length; i++)
            {
                y = Char.Parse(x.Substring(i, 1));
                txtDebug.Text += " ";//,
                txtDebug.Text += y.ToString();
            }
            return true;
        }

        public bool changeIntegerCodeASCII2StringorChar()
        {
            txtDebug.Text = "";
            char y = 'a';
            for (int i = 0; i < cx.Count; i++)
            {
                y = (char)cx[i];
                txtDebug.Text += " ";
                txtDebug.Text += y.ToString();
            }
            return true;
        }


